preset.command:
r[0] 50%,50%
if {$!==1} 100%,100% _rmin=0 _rmax=200 _is_update=1 fi
if $_is_update
  f[1] 0
  _rmax={$_rmax+if($_rmin==$_rmax,1,0)}
  ellipse[1] 50%,50%,$_rmax,$_rmax,0,1,1
  ellipse[1] 50%,50%,$_rmin,$_rmin,0,1,0
  _is_update=0
fi
if {{*,b}&1}
  x={{*,x}-3*w/2}
  y={{*,y}-h/2}
  rmax={round((($x)^2+($y)^2)^0.5)}
  _rmin={if($_rmin>$rmax,$rmax,$_rmin)}
  if {$rmax!=$_rmax} _is_update=1 _rmax=$rmax fi
elif {{*,b}&2}
  x={{*,x}-3*w/2}
  y={{*,y}-h/2}
  rmin={round((($x)^2+($y)^2)^0.5)}
  _rmax={if($rmin>$_rmax,$rmin,$_rmax)}
  if {$rmin!=$_rmin} _is_update=1 _rmin=$rmin fi
elif {{*,b}&4}
  _rmin=0 _rmax=200 _is_update=1
fi
