shift[0,1] 50%,50%,0,0,2
+a[0,1] c norm[-1] +[-1] 1 log[-1] n[-1] 0,255
*[0,1] [2]
shift[0,1] -50%,-50%,0,0,2
ifft[0,1]
rm[1]
n[0] 0,255
*[2] [1] text[-1] "Freq. min : "$_rmin"\nFreq. max : "$_rmax,5,5,13,1,255 r[-1] 100%,100%,1,3 a[0,-1] x
